sys_script_include.script:
var getUserEmailAddress = Class.create();
getUserEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getEmailAddress: function(){
		var userRecord = new GlideRecord("sys_user");
		userRecord.get('sys_id', this.getParameter('sysparm_userID'));
		return userRecord.email + '';
	},

    type: 'getUserEmailAddress'
});
